"use client"

import type React from "react"
import { useState, useRef, useEffect } from "react"
import { cn } from "../../lib/utils"

interface DropdownItem {
  label: React.ReactNode
  onClick: () => void
  type?: never
}

interface DropdownSeparator {
  type: "separator"
  label?: never
  onClick?: never
}

type DropdownMenuItem = DropdownItem | DropdownSeparator

interface DropdownMenuProps {
  trigger: React.ReactNode
  items: DropdownMenuItem[]
  align?: "start" | "end"
}

export function DropdownMenu({ trigger, items, align = "end" }: DropdownMenuProps) {
  const [isOpen, setIsOpen] = useState(false)
  const dropdownRef = useRef<HTMLDivElement>(null)

  useEffect(() => {
    function handleClickOutside(event: MouseEvent) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setIsOpen(false)
      }
    }

    document.addEventListener("mousedown", handleClickOutside)
    return () => document.removeEventListener("mousedown", handleClickOutside)
  }, [])

  return (
    <div className="relative" ref={dropdownRef}>
      <div onClick={() => setIsOpen(!isOpen)}>{trigger}</div>

      {isOpen && (
        <div
          className={cn(
            "absolute top-full mt-2 w-56 rounded-md border border-gray-200 bg-white p-1 shadow-lg z-50",
            align === "end" ? "right-0" : "left-0",
          )}
        >
          {items.map((item, index) =>
            item.type === "separator" ? (
              <div key={index} className="my-1 h-px bg-gray-200" />
            ) : (
              <button
                key={index}
                className="flex w-full items-center rounded-sm px-2 py-1.5 text-sm hover:bg-gray-100 text-left"
                onClick={() => {
                  item.onClick()
                  setIsOpen(false)
                }}
              >
                {item.label}
              </button>
            ),
          )}
        </div>
      )}
    </div>
  )
}
