interface TaxSummary {
  _id?: string;
  taxName: string;
  taxRate: number;
  taxableAmount?: number;
  taxAmount?: number;
}

interface Invoice {
  _id?: string;
  createdAt?: string;
  updatedAt?: string;
  invoiceNumber: string;
  dueDate?: string;
  customerId?: string;
  billingAddress?: {
    address: string;
    city: string;
    state: string;
    country: string;
    pincode: string;
  };
  shippingAddress?: {
    address: string;
    city: string;
    state: string;
    country: string;
    pincode: string;
  };
  items: {
    id: number;
    productId?: string;
    hsn:string;
    quantity: number;
    rate: number;
    discount?: number;
    taxId?: string;
    amount: number;
  }[];
  subTotal: number;
  taxTotal: number;
  discountAmount?: number;
  total: number;
  status: 'draft' | 'sent' | 'paid' | 'partial' | 'overdue' | 'cancelled';
  notes?: string;
  termsAndConditions?: string;
  taxSummary?: TaxSummary[];
  paymentMethod?: string;
  paymentDate?: string;
  amountPaid?: number;
  balanceDue?: number;
  companyId?: string;
  userId?: string;
  isGstInvoice?: boolean;
  isExported?: boolean;
  currency?: string;
}

export type { Invoice, TaxSummary };
