"use client";
import React, { useState, useEffect, useMemo } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import { Button } from "../components/ui/Button";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "../components/ui/Card";
import { ArrowLeft, Plus, Save, Trash2 } from "lucide-react";
import { Address, Invoice, InvoiceItem } from "../interfaces/Invoice";
import { useAuth } from "../contexts/AuthContext";
import { Input } from "../components/ui/Input";
import { Label } from "../components/ui/Label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../components/ui/Select";
import { Textarea } from "../components/ui/Textarea";
import { Switch } from "../components/ui/Switch";
import { format } from "date-fns";
import axios from "axios";
import toast from "react-hot-toast";
import { getCompany } from "../requests/CompanyRequest";
import { getCustomers } from "../requests/customerRequest";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "../components/ui/Table";
import { Product } from "../interfaces/Product";
import { Customer } from "../interfaces/Customer";
import { getProducts } from "../requests/productRequset";

const API_URL = "http://localhost:5000/api/invoice";

type InvoiceStatus =
  | "draft"
  | "sent"
  | "paid"
  | "partial"
  | "overdue"
  | "cancelled";
type Currency = "INR" | "USD" | "EUR";

const defaultAddress: Address = {
  address: "",
  city: "",
  state: "",
  country: "",
  pincode: "",
};

const defaultItem: InvoiceItem = {
  id: Date.now(),
  productId: "",
  hsn: "",
  quantity: 1,
  rate: 0,
  discount: 0,
  taxRate: 0,
  amount: 0,
};

export function NewInvoicePage() {
  const navigate = useNavigate();
  const auth = useAuth();
  const [isLoading, setIsLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);
  const [sameAsBilling, setSameAsBilling] = useState(true);
  const [customers, setCustomers] = useState<Customer[]>([]);
  const [products, setProducts] = useState<Product[]>([]);

  const [invoice, setInvoice] = useState<Invoice>({
    invoiceNumber: "",
    customerId: "",
    dueDate: format(new Date(), "yyyy-MM-dd"),
    items: [{ ...defaultItem }],
    subTotal: 0,
    taxTotal: 0,
    total: 0,
    status: "draft",
    currency: "INR",
    isGstInvoice: false,
    isExported: false,
    billingAddress: { ...defaultAddress },
    shippingAddress: { ...defaultAddress },
  });

  // Calculate totals whenever items change
  const totals = useMemo(() => {
    const subTotal = invoice.items.reduce((sum, item) => sum + item.amount, 0);
    const taxTotal = invoice.items.reduce(
      (sum, item) => sum + (item.amount * (item.taxRate || 0)) / 100,
      0
    );
    return {
      subTotal,
      taxTotal,
      total: subTotal + taxTotal,
    };
  }, [invoice.items]);

  useEffect(() => {
    setInvoice((prev) => ({
      ...prev,
      ...totals,
    }));
  }, [totals]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        if (isNewInvoice) {
          const companyRes = await getCompany(auth.user?._id || "");
          const [customersRes, productRes] = await Promise.all([
            getCustomers(companyRes.company._id),
            getProducts(companyRes.company._id),
          ]);
          setCustomers(customersRes);
          setProducts(productRes);
        } else if (id) {
          const response = await axios.get(`${API_URL}/${id}`, {
            withCredentials: true,
          });
          setInvoice(response.data);
          setSameAsBilling(
            JSON.stringify(response.data.billingAddress) ===
              JSON.stringify(response.data.shippingAddress)
          );
        }
      } catch (error) {
        console.error("Error fetching data:", error);
        toast.error("Failed to load data");
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [id, isNewInvoice, auth.user?._id]);

  const validateForm = () => {
    if (!invoice.invoiceNumber.trim()) {
      toast.error("Invoice number is required");
      return false;
    }
    if (!invoice.customerId) {
      toast.error("Customer is required");
      return false;
    }
    if (invoice.items.some((item) => !item.productId)) {
      toast.error("All items must have a product selected");
      return false;
    }
    return true;
  };

  const handleSave = async () => {
    if (!validateForm()) return;

    try {
      setIsSaving(true);
      if (isNewInvoice) {
        await axios.post(API_URL, invoice, {
          withCredentials: true,
        });
        toast.success("Invoice created successfully");
      } else {
        await axios.put(`${API_URL}/${id}`, invoice, {
          withCredentials: true,
        });
        toast.success("Invoice updated successfully");
      }
      navigate("/invoices");
    } catch (error) {
      console.error("Error saving invoice:", error);
      if (axios.isAxiosError(error)) {
        toast.error(error.response?.data?.message || "Failed to save invoice");
      } else {
        toast.error("An unexpected error occurred");
      }
    } finally {
      setIsSaving(false);
    }
  };

  const handleDelete = async () => {
    if (!id) return;
    if (window.confirm("Are you sure you want to delete this invoice?")) {
      try {
        await axios.delete(`${API_URL}/${id}`, {
          withCredentials: true,
        });
        toast.success("Invoice deleted successfully");
        navigate("/invoices");
      } catch (error) {
        console.error("Error deleting invoice:", error);
        toast.error("Failed to delete invoice");
      }
    }
  };

  const handleInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    const { name, value } = e.target;
    if (
      name.startsWith("billingAddress.") ||
      name.startsWith("shippingAddress.")
    ) {
      const [addressType, field] = name.split(".");
      setInvoice((prev) => ({
        ...prev,
        [addressType]: {
          ...prev[addressType as "billingAddress" | "shippingAddress"],
          [field]: value,
        },
      }));
    } else {
      setInvoice((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleCheckboxChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, checked } = e.target;
    setInvoice((prev) => ({ ...prev, [name]: checked }));
  };

  const addItem = () => {
    setInvoice((prev) => ({
      ...prev,
      items: [
        ...prev.items,
        {
          ...defaultItem,
          id: Date.now() + Math.floor(Math.random() * 1000),
        },
      ],
    }));
  };

  const removeItem = (id: number) => {
    setInvoice((prev) => ({
      ...prev,
      items: prev.items.filter((item) => item.id !== id),
    }));
  };

  const updateItem = (id: number, field: keyof InvoiceItem, value: any) => {
    setInvoice((prev) => ({
      ...prev,
      items: prev.items.map((item) => {
        if (item.id === id) {
          const updatedItem = { ...item, [field]: value };

          // Recalculate amount if relevant fields change
          if (["quantity", "rate", "discount"].includes(field)) {
            const subtotal = updatedItem.quantity * updatedItem.rate;
            const discountAmount =
              (subtotal * (updatedItem.discount || 0)) / 100;
            updatedItem.amount = subtotal - discountAmount;
          }

          return updatedItem;
        }
        return item;
      }),
    }));
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-screen">
        Loading...
      </div>
    );
  }

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <Button variant="ghost" size="icon" asChild>
            <Link to="/invoices">
              <ArrowLeft className="h-4 w-4" />
            </Link>
          </Button>
          <h1 className="text-2xl font-bold tracking-tight">
            {isNewInvoice ? "New Invoice" : `Invoice #${invoice.invoiceNumber}`}
          </h1>
        </div>
        <div className="flex items-center gap-2">
          {!isNewInvoice && (
            <Button
              variant="secondary"
              onClick={handleDelete}
              disabled={isSaving}
            >
              <Trash2 className="mr-2 h-4 w-4" />
              Delete
            </Button>
          )}
          <Button onClick={handleSave} disabled={isSaving}>
            {isSaving ? (
              "Saving..."
            ) : isNewInvoice ? (
              <>
                <Plus className="mr-2 h-4 w-4" />
                Create Invoice
              </>
            ) : (
              <>
                <Save className="mr-2 h-4 w-4" />
                Save Changes
              </>
            )}
          </Button>
        </div>
      </div>

      <div className="grid gap-4 md:grid-cols-2">
        <Card>
          <CardHeader>
            <CardTitle>Invoice Information</CardTitle>
            <CardDescription>Basic invoice details</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid gap-4 md:grid-cols-2">
              <div className="space-y-2">
                <Label htmlFor="invoiceNumber">Invoice Number *</Label>
                <Input
                  id="invoiceNumber"
                  name="invoiceNumber"
                  value={invoice.invoiceNumber}
                  onChange={handleInputChange}
                  placeholder="e.g., INV-001"
                  required
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="dueDate">Due Date</Label>
                <Input
                  id="dueDate"
                  name="dueDate"
                  type="date"
                  value={invoice.dueDate}
                  onChange={handleInputChange}
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="status">Status</Label>
                <Select
                  value={invoice.status}
                  onValueChange={(value: InvoiceStatus) =>
                    setInvoice((prev) => ({ ...prev, status: value }))
                  }
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Select status" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="draft">Draft</SelectItem>
                    <SelectItem value="sent">Sent</SelectItem>
                    <SelectItem value="paid">Paid</SelectItem>
                    <SelectItem value="partial">Partial</SelectItem>
                    <SelectItem value="overdue">Overdue</SelectItem>
                    <SelectItem value="cancelled">Cancelled</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div className="space-y-2">
                <Label htmlFor="currency">Currency</Label>
                <Select
                  value={invoice.currency}
                  onValueChange={(value: Currency) =>
                    setInvoice((prev) => ({ ...prev, currency: value }))
                  }
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Select currency" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="INR">INR</SelectItem>
                    <SelectItem value="USD">USD</SelectItem>
                    <SelectItem value="EUR">EUR</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
            <div className="flex items-center space-x-2">
              <Switch
                id="isGstInvoice"
                checked={invoice.isGstInvoice}
                onCheckedChange={(checked) =>
                  setInvoice((prev) => ({ ...prev, isGstInvoice: checked }))
                }
              />
              <Label htmlFor="isGstInvoice">GST Invoice</Label>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Customer Information</CardTitle>
            <CardDescription>Customer and address details</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="customerId">Customer *</Label>
              <Select
                value={invoice.customerId}
                onValueChange={(value) => {
                  const selectedCustomer = customers.find(
                    (c) => c._id === value
                  );
                  setInvoice((prev) => ({
                    ...prev,
                    customerId: value,
                    billingAddress: selectedCustomer?.billingAddress || {
                      ...defaultAddress,
                    },
                  }));
                  if (sameAsBilling) {
                    setInvoice((prev) => ({
                      ...prev,
                      shippingAddress: selectedCustomer?.billingAddress || {
                        ...defaultAddress,
                      },
                    }));
                  }
                }}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select customer" />
                </SelectTrigger>
                <SelectContent>
                  {customers.map((customer) => (
                    <SelectItem key={customer._id!} value={customer._id!}>
                      {customer.fullName}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-4">
              <h3 className="font-medium">Billing Address</h3>
              <div className="grid gap-4">
                <Input
                  name="billingAddress.address"
                  value={invoice.billingAddress?.address || ""}
                  onChange={handleInputChange}
                  placeholder="Address"
                />
                <div className="grid gap-4 md:grid-cols-2">
                  <Input
                    name="billingAddress.city"
                    value={invoice.billingAddress?.city || ""}
                    onChange={handleInputChange}
                    placeholder="City"
                  />
                  <Input
                    name="billingAddress.state"
                    value={invoice.billingAddress?.state || ""}
                    onChange={handleInputChange}
                    placeholder="State"
                  />
                </div>
                <div className="grid gap-4 md:grid-cols-2">
                  <Input
                    name="billingAddress.country"
                    value={invoice.billingAddress?.country || ""}
                    onChange={handleInputChange}
                    placeholder="Country"
                  />
                  <Input
                    name="billingAddress.pincode"
                    value={invoice.billingAddress?.pincode || ""}
                    onChange={handleInputChange}
                    placeholder="Pincode"
                  />
                </div>
              </div>
            </div>

            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <h3 className="font-medium">Shipping Address</h3>
                <div className="flex items-center space-x-2">
                  <Switch
                    id="sameAsBilling"
                    checked={sameAsBilling}
                    onCheckedChange={(checked) => {
                      setSameAsBilling(checked);
                      if (checked) {
                        setInvoice((prev) => ({
                          ...prev,
                          shippingAddress: {
                            address: prev.billingAddress?.address || "",
                            city: prev.billingAddress?.city || "",
                            state: prev.billingAddress?.state || "",
                            country: prev.billingAddress?.country || "",
                            pincode: prev.billingAddress?.pincode || "",
                          },
                        }));
                      }
                    }}
                  />
                  <Label htmlFor="sameAsBilling">Same as billing</Label>
                </div>
              </div>

              {!sameAsBilling && (
                <div className="grid gap-4">
                  <Input
                    name="shippingAddress.address"
                    value={invoice.shippingAddress?.address || ""}
                    onChange={handleInputChange}
                    placeholder="Address"
                  />
                  <div className="grid gap-4 md:grid-cols-2">
                    <Input
                      name="shippingAddress.city"
                      value={invoice.shippingAddress?.city || ""}
                      onChange={handleInputChange}
                      placeholder="City"
                    />
                    <Input
                      name="shippingAddress.state"
                      value={invoice.shippingAddress?.state || ""}
                      onChange={handleInputChange}
                      placeholder="State"
                    />
                  </div>
                  <div className="grid gap-4 md:grid-cols-2">
                    <Input
                      name="shippingAddress.country"
                      value={invoice.shippingAddress?.country || ""}
                      onChange={handleInputChange}
                      placeholder="Country"
                    />
                    <Input
                      name="shippingAddress.pincode"
                      value={invoice.shippingAddress?.pincode || ""}
                      onChange={handleInputChange}
                      placeholder="Pincode"
                    />
                  </div>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>Invoice Items</CardTitle>
              <CardDescription>
                Add products or services to this invoice.
              </CardDescription>
            </div>
            <Button onClick={addItem} size="sm">
              <Plus className="mr-2 h-4 w-4" />
              Add Item
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          <div className="rounded-md border overflow-x-auto">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead className="w-[200px]">Product/Service *</TableHead>
                  <TableHead className="w-[100px]">HSN</TableHead>
                  <TableHead className="w-[80px]">Qty</TableHead>
                  <TableHead className="w-[100px]">Rate</TableHead>
                  <TableHead className="w-[80px]">Disc%</TableHead>
                  <TableHead className="w-[80px]">Tax%</TableHead>
                  <TableHead className="w-[100px]">Amount</TableHead>
                  <TableHead className="w-[50px]"></TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {invoice.items.map((item) => (
                  <TableRow key={item.id}>
                    <TableCell>
                      <Select
                        value={item.productId}
                        onValueChange={(value) => {
                          const selectedProduct = products.find(
                            (p) => p._id === value
                          );
                          updateItem(item.id, "productId", value);
                          if (selectedProduct) {
                            updateItem(
                              item.id,
                              "hsn",
                              selectedProduct.hsn || ""
                            );
                            updateItem(
                              item.id,
                              "rate",
                              selectedProduct.price || 0
                            );
                          }
                        }}
                      >
                        <SelectTrigger>
                          <SelectValue placeholder="Select product" />
                        </SelectTrigger>
                        <SelectContent>
                          {products.map((product) => (
                            <SelectItem key={product._id!} value={product._id!}>
                              {product.name}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </TableCell>
                    <TableCell>
                      <Input
                        value={item.hsn}
                        onChange={(e) =>
                          updateItem(item.id, "hsn", e.target.value)
                        }
                        placeholder="HSN"
                      />
                    </TableCell>
                    <TableCell>
                      <Input
                        type="number"
                        value={item.quantity}
                        onChange={(e) =>
                          updateItem(
                            item.id,
                            "quantity",
                            Number(e.target.value)
                          )
                        }
                        min="1"
                      />
                    </TableCell>
                    <TableCell>
                      <Input
                        type="number"
                        value={item.rate}
                        onChange={(e) =>
                          updateItem(item.id, "rate", Number(e.target.value))
                        }
                        min="0"
                        step="0.01"
                      />
                    </TableCell>
                    <TableCell>
                      <Input
                        type="number"
                        value={item.discount}
                        onChange={(e) =>
                          updateItem(
                            item.id,
                            "discount",
                            Number(e.target.value)
                          )
                        }
                        min="0"
                        max="100"
                      />
                    </TableCell>
                    <TableCell>
                      <Select
                        value={item.taxRate.toString()}
                        onValueChange={(value) =>
                          updateItem(item.id, "taxRate", Number(value))
                        }
                      >
                        <SelectTrigger>
                          <SelectValue placeholder="Tax" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="0">0%</SelectItem>
                          <SelectItem value="5">5%</SelectItem>
                          <SelectItem value="12">12%</SelectItem>
                          <SelectItem value="18">18%</SelectItem>
                          <SelectItem value="28">28%</SelectItem>
                        </SelectContent>
                      </Select>
                    </TableCell>
                    <TableCell>
                      <Input
                        value={item.amount.toFixed(2)}
                        readOnly
                        className="font-medium"
                      />
                    </TableCell>
                    <TableCell>
                      {invoice.items.length > 1 && (
                        <Button
                          variant="ghost"
                          size="icon"
                          onClick={() => removeItem(item.id)}
                        >
                          <Trash2 className="h-4 w-4 text-red-500" />
                        </Button>
                      )}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Summary</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex justify-end">
            <div className="w-full max-w-md space-y-2">
              <div className="flex justify-between">
                <span className="text-muted-foreground">Subtotal:</span>
                <span>{invoice.subTotal.toFixed(2)}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-muted-foreground">Tax:</span>
                <span>{invoice.taxTotal.toFixed(2)}</span>
              </div>
              <div className="flex justify-between pt-2 border-t font-bold">
                <span>Total:</span>
                <span>{invoice.total.toFixed(2)}</span>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Additional Information</CardTitle>
          <CardDescription>Notes and terms</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="notes">Notes</Label>
            <Textarea
              id="notes"
              name="notes"
              value={invoice.notes || ""}
              onChange={handleInputChange}
              placeholder="Add any additional notes..."
              rows={3}
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="termsAndConditions">Terms and Conditions</Label>
            <Textarea
              id="termsAndConditions"
              name="termsAndConditions"
              value={invoice.termsAndConditions || ""}
              onChange={handleInputChange}
              placeholder="Add terms and conditions..."
              rows={3}
            />
          </div>
        </CardContent>
      </Card>
    </div>
  );
}
