interface Address {
  address: string;
  city: string;
  state: string;
  country: string;
  pincode: string;
}

interface InvoiceItem {
  id: number;
  productId: string;
  hsn: string;
  quantity: number;
  rate: number;
  discount: number;
  taxRate: number;
  amount: number;
}

const defaultAddress: Address = {
  address: "",
  city: "",
  state: "",
  country: "",
  pincode: "",
};

const defaultItem: InvoiceItem = {
  id: Date.now(),
  productId: "",
  hsn: "",
  quantity: 1,
  rate: 0,
  discount: 0,
  taxRate: 0,
  amount: 0,
};

interface TaxSummary {
  _id?: string;
  taxName: string;
  taxRate: number;
  taxableAmount?: number;
  taxAmount?: number;
}

interface Invoice {
  _id?: string;
  createdAt?: string;
  updatedAt?: string;
  invoiceNumber: string;
  dueDate?: string;
  customerId?: string;
  billingAddress?: Address;
  shippingAddress?: Address;
  items: InvoiceItem[];
  subTotal: number;
  taxTotal: number;
  discountAmount?: number;
  total: number;
  status: "draft" | "sent" | "paid" | "partial" | "overdue" | "cancelled";
  notes?: string;
  termsAndConditions?: string;
  taxSummary?: TaxSummary[];
  paymentMethod?: string;
  paymentDate?: string;
  amountPaid?: number;
  balanceDue?: number;
  companyId?: string;
  userId?: string;
  isGstInvoice?: boolean;
  isExported?: boolean;
  currency?: string;
}

export type {
  Invoice,
  TaxSummary,
  InvoiceItem,
  Address,
  defaultAddress,
  defaultItem,
};
