"use client";

import React, { useState } from "react";
import { Bell, Search, Moon, Sun, User } from "lucide-react";
import { Button } from "./ui/Button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "./ui/DropdownMenu";
import { Input } from "./ui/Input";
import { Avatar, AvatarImage, AvatarFallback } from "./ui/Avatar";
import { useAuth } from "../contexts/AuthContext";
import { useTheme } from "./ui/theme-provider";
import { useNavigate } from "react-router-dom";
import { DropdownMenuLabel } from "@radix-ui/react-dropdown-menu";

export function Header() {
  const [searchQuery, setSearchQuery] = useState("");
  const { user, logout } = useAuth();
  const { theme, setTheme } = useTheme();
  const isDark = theme === "dark";
  const navigate = useNavigate();

  return (
    <header className="sticky top-0 z-30 flex h-16 items-center gap-4 border-b bg-white px-4 dark:bg-black md:px-6">
      <div className="hidden md:block md:w-64"></div>
      <div className="w-full flex items-center justify-between">
        <form className="hidden md:block md:flex-1 md:max-w-sm">
          <div className="relative">
            <Search className="absolute left-2.5 top-2.5 h-4 w-4 text-gray-500" />
            <Input
              type="search"
              placeholder="Search..."
              className="w-full pl-8"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              aria-label="Search"
            />
          </div>
        </form>

        {/* Theme Toggle */}
        <div
          onClick={() => setTheme(isDark ? "light" : "dark")}
          className="cursor-pointer relative w-6 h-6 mx-4"
          aria-label="Toggle theme"
        >
          <Sun
            className={`absolute h-6 w-6 text-yellow-500 transition-opacity duration-300 ${
              isDark ? "opacity-100" : "opacity-0"
            }`}
          />
          <Moon
            className={`absolute h-6 w-6 text-blue-500 transition-opacity duration-300 ${
              isDark ? "opacity-0" : "opacity-100"
            }`}
          />
        </div>

        <div className="flex items-center gap-2 md:ml-auto">
          {/* Notification Button */}
          <Button
            variant="ghost"
            size="icon"
            className="relative"
            aria-label="Notifications"
          >
            <Bell className="h-5 w-5" />
            <span className="absolute right-1 top-1 flex h-2 w-2 rounded-full bg-blue-600"></span>
          </Button>

          {/* User Dropdown */}
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button
                variant="ghost"
                size="icon"
                className="rounded-full"
                aria-label="User menu"
              >
                <Avatar className="h-8 w-8">
                  <AvatarImage src={user?.image} alt="User profile picture" />
                  <AvatarFallback>
                    {getInitials(user?.fullName ?? "") || (
                      <User className="h-4 w-4" />
                    )}
                  </AvatarFallback>
                </Avatar>
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuLabel>
                <div className="flex flex-col space-y-1">
                  <p className="text-sm font-medium">{user?.fullName}</p>
                  <p className="text-xs text-gray-500">{user?.email}</p>
                </div>
              </DropdownMenuLabel>
              <DropdownMenuSeparator />
              <DropdownMenuItem onSelect={() => navigate("/profile")}>
                Profile
              </DropdownMenuItem>
              <DropdownMenuItem onSelect={() => navigate("/settings")}>
                Settings
              </DropdownMenuItem >
              <DropdownMenuSeparator />
              <DropdownMenuItem onSelect={logout}>Logout</DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
    </header>
  );
}

function getInitials(name: string) {
  if (!name) return "";
  const words = name.trim().split(" ");
  if (words.length === 1) return words[0][0].toUpperCase();
  return (words[0][0] + words[1][0]).toUpperCase();
}
