"use client";
import React, { useState, useEffect } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import { Button } from "../components/ui/Button";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "../components/ui/Card";
import { ArrowLeft, Plus, Save, Trash2 } from "lucide-react";
import { Invoice } from "../interfaces/Invoice";
import { useAuth } from "../contexts/AuthContext";
import { Input } from "../components/ui/Input";
import { Label } from "../components/ui/Label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../components/ui/Select";
import { Textarea } from "../components/ui/Textarea";
import { Switch } from "../components/ui/Switch";
import { format } from "date-fns";
import axios from "axios";
import toast from "react-hot-toast";
import { getCompany } from "../requests/CompanyRequest";
import { getCustomers } from "../requests/customerRequest";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "../components/ui/Table";

const API_URL = "http://localhost:5000/api/invoice";

interface Address {
  address: string;
  city: string;
  state: string;
  country: string;
  pincode: string;
}

const defaultAddress: Address = {
  address: "",
  city: "",
  state: "",
  country: "",
  pincode: "",
};

export function InvoiceDetailPage() {
  const id = window.location.href.split("/").pop();
  const navigate = useNavigate();
  const auth = useAuth();
  const [invoice, setInvoice] = useState<Invoice>({
    invoiceNumber: "",
    items: [],
    subTotal: 0,
    taxTotal: 0,
    total: 0,
    status: "draft",
    currency: "INR",
    isGstInvoice: false,
    isExported: false,
    billingAddress: defaultAddress,
    shippingAddress: defaultAddress,
  });
  const [items, setItems] = useState<Invoice["items"]>([
    {
      id: 1,
      quantity: 1,
      productId: "kb001",
      rate: 0,
      discount: 0,
      taxId: "",
      amount: 0,
    },
  ])
  const [isLoading, setIsLoading] = useState(true);
  const [isNewInvoice] = useState(!id);
  const [sameAsBilling, setSameAsBilling] = useState(true);
  const [customers, setCustomers] = useState<any[]>([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        if (id == "new") {
          const companyRes = await getCompany(auth.user?._id || "");
          const customersRes = await getCustomers(companyRes.company._id);
          setCustomers(customersRes);
        } else if (!isNewInvoice && id) {
          const response = await axios.get(`${API_URL}/${id}`, {
            withCredentials: true,
          });
          setInvoice(response.data);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
        toast.error("Failed to load data");
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [id, isNewInvoice]);

  const handleSave = async () => {
    try {
      if (isNewInvoice) {
        await axios.post(API_URL, invoice, {
          withCredentials: true,
        });
        toast.success("Invoice created successfully");
      } else {
        await axios.put(`${API_URL}/${id}`, invoice, {
          withCredentials: true,
        });
        toast.success("Invoice updated successfully");
      }
      navigate("/invoices");
    } catch (error) {
      console.error("Error saving invoice:", error);
      toast.error("Failed to save invoice");
    }
  };

  const handleDelete = async () => {
    if (!id) return;
    if (window.confirm("Are you sure you want to delete this invoice?")) {
      try {
        await axios.delete(`${API_URL}/${id}`, {
          withCredentials: true,
        });
        toast.success("Invoice deleted successfully");
        navigate("/invoices");
      } catch (error) {
        console.error("Error deleting invoice:", error);
        toast.error("Failed to delete invoice");
      }
    }
  };

  const handleInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    const { name, value } = e.target;
    if (
      name.startsWith("billingAddress.") ||
      name.startsWith("shippingAddress.")
    ) {
      const [addressType, field] = name.split(".");
      setInvoice((prev) => ({
        ...prev,
        [addressType]: {
          ...(prev[addressType as "billingAddress" | "shippingAddress"] ||
            defaultAddress),
          [field]: value,
        },
      }));
    } else {
      setInvoice((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleCheckboxChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, checked } = e.target;
    setInvoice((prev) => ({ ...prev, [name]: checked }));
    if (name === "sameAsBilling" && checked) {
      setInvoice((prev) => ({
        ...prev,
        shippingAddress: { ...(prev.billingAddress || defaultAddress) },
      }));
    }
  };

  const addItem = () => {

  const updateItem = (id: number, field: string, value: any) => {
    setItems(items.map((item) => item.id === id ? { ...item, [field]: value } : item));
  }

  const removeItem = (id: number) => {
    setItems(items.filter((item) => item.id !== id));
  }

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <Button variant="ghost" size="icon">
            <Link to="/invoices">
              <ArrowLeft className="h-4 w-4" />
            </Link>
          </Button>
          <h1 className="text-2xl font-bold tracking-tight">
            {isNewInvoice ? "New Invoice" : `Invoice #${id}`}
          </h1>
        </div>
        <div className="flex items-center gap-2">
          {!isNewInvoice && (
            <Button
              variant="default"
              onClick={handleDelete}
              className="bg-red-600 hover:bg-red-700"
            >
              <Trash2 className="mr-2 h-4 w-4" />
              Delete
            </Button>
          )}
          <Button onClick={handleSave}>
            {isNewInvoice ? (
              <>
                <Plus className="mr-2 h-4 w-4" />
                Create Invoice
              </>
            ) : (
              <>
                <Save className="mr-2 h-4 w-4" />
                Save Changes
              </>
            )}
          </Button>
        </div>
      </div>

      <div className="grid gap-4 md:grid-cols-2">
        <Card>
          <CardHeader>
            <CardTitle>Invoice Information</CardTitle>
            <CardDescription>Basic invoice details</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid gap-4 md:grid-cols-2">
              <div className="space-y-2">
                <Label htmlFor="invoiceNumber">Invoice Number</Label>
                <Input
                  id="invoiceNumber"
                  name="invoiceNumber"
                  value={invoice.invoiceNumber}
                  onChange={handleInputChange}
                  placeholder="e.g., INV-001"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="dueDate">Due Date</Label>
                <Input
                  id="dueDate"
                  name="dueDate"
                  type="date"
                  value={invoice.dueDate}
                  onChange={handleInputChange}
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="status">Status</Label>
                <Select
                  value={invoice.status}
                  onValueChange={(value) =>
                    setInvoice((prev) => ({ ...prev, status: value as any }))
                  }
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Select status" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="draft">Draft</SelectItem>
                    <SelectItem value="sent">Sent</SelectItem>
                    <SelectItem value="paid">Paid</SelectItem>
                    <SelectItem value="partial">Partial</SelectItem>
                    <SelectItem value="overdue">Overdue</SelectItem>
                    <SelectItem value="cancelled">Cancelled</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div className="space-y-2">
                <Label htmlFor="currency">Currency</Label>
                <Select
                  value={invoice.currency}
                  onValueChange={(value) =>
                    setInvoice((prev) => ({ ...prev, currency: value }))
                  }
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Select currency" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="INR">INR</SelectItem>
                    <SelectItem value="USD">USD</SelectItem>
                    <SelectItem value="EUR">EUR</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
            <div className="flex items-center space-x-2">
              <Switch
                id="isGstInvoice"
                checked={invoice.isGstInvoice}
                onCheckedChange={(checked) =>
                  setInvoice((prev) => ({ ...prev, isGstInvoice: checked }))
                }
              />
              <Label htmlFor="isGstInvoice">GST Invoice</Label>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Customer Information</CardTitle>
            <CardDescription>Customer and address details</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="customerId">Customer</Label>
              <Select
                value={invoice.customerId}
                onValueChange={(value) =>
                  setInvoice((prev) => ({ ...prev, customerId: value }))
                }
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select customer" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem key="default" value="default" >
                    Select Customer
                  </SelectItem>
                  {customers.map((customer) => (
                    <SelectItem key={customer._id} value={customer._id}>
                      {customer.fullName}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-4">
              <h3 className="font-medium">Billing Address</h3>
              <div className="grid gap-4">
                <Input
                  name="billingAddress.address"
                  value={invoice.billingAddress?.address || ""}
                  onChange={handleInputChange}
                  placeholder="Address"
                />
                <div className="grid gap-4 md:grid-cols-2">
                  <Input
                    name="billingAddress.city"
                    value={invoice.billingAddress?.city || ""}
                    onChange={handleInputChange}
                    placeholder="City"
                  />
                  <Input
                    name="billingAddress.state"
                    value={invoice.billingAddress?.state || ""}
                    onChange={handleInputChange}
                    placeholder="State"
                  />
                </div>
                <div className="grid gap-4 md:grid-cols-2">
                  <Input
                    name="billingAddress.country"
                    value={invoice.billingAddress?.country || ""}
                    onChange={handleInputChange}
                    placeholder="Country"
                  />
                  <Input
                    name="billingAddress.pincode"
                    value={invoice.billingAddress?.pincode || ""}
                    onChange={handleInputChange}
                    placeholder="Pincode"
                  />
                </div>
              </div>
            </div>

            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <h3 className="font-medium">Shipping Address</h3>
                <div className="flex items-center space-x-2">
                  <Switch
                    id="sameAsBilling"
                    checked={sameAsBilling}
                    onCheckedChange={(checked) => {
                      setSameAsBilling(checked);
                      if (checked) {
                        setInvoice((prev) => ({
                          ...prev,
                          shippingAddress: {
                            ...(prev.billingAddress || defaultAddress),
                          },
                        }));
                      }
                    }}
                  />
                  <Label htmlFor="sameAsBilling">Same as billing</Label>
                </div>
              </div>
              {!sameAsBilling && (
                <div className="grid gap-4">
                  <Input
                    name="shippingAddress.address"
                    value={invoice.shippingAddress?.address || ""}
                    onChange={handleInputChange}
                    placeholder="Address"
                  />
                  <div className="grid gap-4 md:grid-cols-2">
                    <Input
                      name="shippingAddress.city"
                      value={invoice.shippingAddress?.city || ""}
                      onChange={handleInputChange}
                      placeholder="City"
                    />
                    <Input
                      name="shippingAddress.state"
                      value={invoice.shippingAddress?.state || ""}
                      onChange={handleInputChange}
                      placeholder="State"
                    />
                  </div>
                  <div className="grid gap-4 md:grid-cols-2">
                    <Input
                      name="shippingAddress.country"
                      value={invoice.shippingAddress?.country || ""}
                      onChange={handleInputChange}
                      placeholder="Country"
                    />
                    <Input
                      name="shippingAddress.pincode"
                      value={invoice.shippingAddress?.pincode || ""}
                      onChange={handleInputChange}
                      placeholder="Pincode"
                    />
                  </div>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <div>
                  <CardTitle>Invoice Items</CardTitle>
                  <CardDescription>Add products or services to this invoice.</CardDescription>
                </div>
                <Button onClick={addItem} size="sm">
                  <Plus className="mr-2 h-4 w-4" />
                  Add Item
                </Button>
              </div>
            </CardHeader>
            <CardContent>
              <div className="rounded-md border">
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead className="w-[200px]">Product/Service</TableHead>
                      <TableHead className="w-[80px]">HSN</TableHead>
                      <TableHead className="w-[80px]">Qty</TableHead>
                      <TableHead className="w-[80px]">Unit</TableHead>
                      <TableHead className="w-[100px]">Rate</TableHead>
                      <TableHead className="w-[80px]">Disc%</TableHead>
                      <TableHead className="w-[80px]">Tax%</TableHead>
                      <TableHead className="w-[100px]">Amount</TableHead>
                      <TableHead className="w-[50px]"></TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {items.map((item) => (
                      <TableRow key={item.id}>
                        <TableCell>
                          <Select onValueChange={(value) => updateItem(item.id, "productId", value)}>
                            <SelectTrigger>
                              <SelectValue placeholder="Select product" />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="kb001">Wireless Keyboard</SelectItem>
                              <SelectItem value="usbc001">USB-C Cable</SelectItem>
                              <SelectItem value="ms001">Wireless Mouse</SelectItem>
                              <SelectItem value="svc001">Technical Support</SelectItem>
                            </SelectContent>
                          </Select>
                        </TableCell>
                        <TableCell>
                          <Input
                            value={item.taxId}
                            onChange={(e) => updateItem(item.id, "taxId", e.target.value)}
                            placeholder="HSN"
                          />
                        </TableCell>
                        <TableCell>
                          <Input
                            type="number"
                            value={item.quantity}
                            onChange={(e) => updateItem(item.id, "quantity", Number(e.target.value))}
                            min="1"
                          />
                        </TableCell>
                        <TableCell>
                          <Select value={item.productId} onValueChange={(value) => updateItem(item.id, "productId", value)}>
                            <SelectTrigger>
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="Pcs">Pcs</SelectItem>
                              <SelectItem value="Kg">Kg</SelectItem>
                              <SelectItem value="Service">Service</SelectItem>
                            </SelectContent>
                          </Select>
                        </TableCell>
                        <TableCell>
                          <Input
                            type="number"
                            value={item.rate}
                            onChange={(e) => updateItem(item.id, "rate", Number(e.target.value))}
                            min="0"
                            step="0.01"
                          />
                        </TableCell>
                        <TableCell>
                          <Input
                            type="number"
                            value={item.discount}
                            onChange={(e) => updateItem(item.id, "discount", Number(e.target.value))}
                            min="0"
                            max="100"
                          />
                        </TableCell>
                        <TableCell>
                          <Select
                            value={item.taxId}
                            onValueChange={(value) => updateItem(item.id, "taxId", value)}
                          >
                            <SelectTrigger>
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="0">0%</SelectItem>
                              <SelectItem value="5">5%</SelectItem>
                              <SelectItem value="12">12%</SelectItem>
                              <SelectItem value="18">18%</SelectItem>
                              <SelectItem value="28">28%</SelectItem>
                            </SelectContent>
                          </Select>
                        </TableCell>
                        <TableCell>
                          <Input value={item.amount} readOnly />
                        </TableCell>
                        <TableCell>
                          {items.length > 1 && (
                            <Button variant="ghost" size="icon" onClick={() => removeItem(item.id)}>
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          )}
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </div>
            </CardContent>
          </Card>

      <Card>
        <CardHeader>
          <CardTitle>Additional Information</CardTitle>
          <CardDescription>Notes and terms</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="notes">Notes</Label>
            <Textarea
              id="notes"
              name="notes"
              value={invoice.notes}
              onChange={handleInputChange}
              placeholder="Add any additional notes..."
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="termsAndConditions">Terms and Conditions</Label>
            <Textarea
              id="termsAndConditions"
              name="termsAndConditions"
              value={invoice.termsAndConditions}
              onChange={handleInputChange}
              placeholder="Add terms and conditions..."
            />
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Payment Information</CardTitle>
          <CardDescription>Payment details and summary</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {/* TODO: Add payment information */}
            <p className="text-gray-500">
              Payment information will appear here.
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}
}
